const body = $input.first().json.body;
const dataUrl = body.screenshotDataUrl;

// Parse data URL: data:image/jpeg;base64,/9j/4AAQ...
const matches = dataUrl.match(/^data:([^;]+);base64,(.+)$/);
if (!matches) throw new Error('Invalid data URL format');

const mimeType = matches[1];
const base64Data = matches[2];
const buffer = Buffer.from(base64Data, 'base64');

// Determine file extension
const extMap = { 'image/jpeg': 'jpg', 'image/png': 'png', 'image/webp': 'webp', 'image/gif': 'gif' };
const ext = extMap[mimeType] || 'jpg';

const now = new Date();
const ts = now.toISOString().replace(/[:.]/g, '-').slice(0, 19);
const fileName = `feedback-${body.category || 'general'}-${ts}.${ext}`;

const binaryData = await this.helpers.prepareBinaryData(buffer, fileName, mimeType);

return [{
  json: { ...body, _fileName: fileName },
  binary: { data: binaryData }
}];

const driveResult = $input.first().json;
const fileId = driveResult.id;
const screenshotUrl = fileId ? 'https://drive.google.com/file/d/' + fileId + '/view' : '';
const viewUrl = fileId ? 'https://lh3.googleusercontent.com/d/' + fileId + '=w800' : '';

// Pass through original body from webhook
const body = $('Has Screenshot?').first().json.body;

return [{ json: { ...body, screenshotUrl, screenshotViewUrl: viewUrl, _driveFileId: fileId } }];

const body = $input.first().json;

const now = new Date();
const dateStr = now.toLocaleDateString('en-US', { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' });
const timeStr = now.toLocaleTimeString('en-US', { hour: '2-digit', minute: '2-digit', hour12: true });

const labelMap = { 'bug': 'BUG', 'feature_request': 'FEATURE REQUEST', 'feedback': 'FEEDBACK', 'improvement': 'IMPROVEMENT' };
const label = labelMap[body.category] || (body.category || 'UNKNOWN').toUpperCase();

const rating = body.rating || 0;
const stars = '\u2B50'.repeat(rating) + '\u2606'.repeat(5 - rating);

// Telegram message (HTML format)
let telegram = `<b>[${label}]</b> \u2014 QuotaHit Feedback\n\n`;
telegram += `<b>Rating:</b> ${stars} (${rating}/5)\n`;
telegram += `<b>From:</b> ${body.name || 'Anonymous'} (${body.email || 'No email'})\n`;
telegram += `<b>Page:</b> ${body.pageUrl || 'Unknown'}\n\n`;
telegram += `<b>Feedback:</b>\n${body.primaryText || '(empty)'}\n`;
if (body.secondaryText) telegram += `\n<b>Details:</b>\n${body.secondaryText}\n`;
if (body.tertiaryText) telegram += `\n<b>Additional:</b>\n${body.tertiaryText}\n`;
if (body.screenshotUrl) telegram += `\n\u{1F4F8} <a href="${body.screenshotUrl}">View Screenshot</a>`;
telegram += `\n\n\u23F0 ${dateStr} at ${timeStr}`;

// Gmail HTML
let html = '<div style="font-family:Arial,sans-serif;max-width:640px;margin:0 auto;padding:20px;">';
const color = body.category === 'bug' ? '#ef4444' : body.category === 'feature_request' ? '#3b82f6' : '#22c55e';
const bg = body.category === 'bug' ? '#fef2f2' : body.category === 'feature_request' ? '#f0f9ff' : '#f0fdf4';
html += `<div style="background:${bg};border-left:4px solid ${color};padding:16px;border-radius:4px;margin-bottom:20px;">`;
html += `<strong style="font-size:16px;">[${label}]</strong> &mdash; Rating: ${stars} (${rating}/5)</div>`;
html += '<table style="width:100%;border-collapse:collapse;margin-bottom:20px;">';
html += `<tr><td style="padding:8px;font-weight:bold;width:120px;">From:</td><td style="padding:8px;">${body.name || 'Anonymous'} (${body.email || 'No email'})</td></tr>`;
html += `<tr><td style="padding:8px;font-weight:bold;">Category:</td><td style="padding:8px;">${label}</td></tr>`;
html += `<tr><td style="padding:8px;font-weight:bold;">Page:</td><td style="padding:8px;"><a href="${body.pageUrl || '#'}">${body.pageUrl || 'Unknown'}</a></td></tr>`;
html += `<tr><td style="padding:8px;font-weight:bold;">Timestamp:</td><td style="padding:8px;">${dateStr} at ${timeStr}</td></tr>`;
html += '</table>';
html += `<h3>Feedback</h3><p style="background:#f9fafb;padding:12px;border-radius:6px;">${body.primaryText || '(empty)'}</p>`;
if (body.secondaryText) html += `<h3>Additional Details</h3><p style="background:#f9fafb;padding:12px;border-radius:6px;">${body.secondaryText}</p>`;
if (body.tertiaryText) html += `<h3>Other Notes</h3><p style="background:#f9fafb;padding:12px;border-radius:6px;">${body.tertiaryText}</p>`;
if (body.screenshotViewUrl) {
  html += `<h3>Screenshot</h3><a href="${body.screenshotUrl}"><img src="${body.screenshotViewUrl}" style="max-width:100%;border-radius:8px;border:1px solid #e5e7eb;" /></a>`;
}
html += '</div>';

const emailSubject = `[${label}] Feedback from ${body.name || 'Anonymous'} \u2014 QuotaHit`;

const sheetRow = {
  Date: `${dateStr} at ${timeStr}`,
  Category: label,
  Rating: rating,
  'Primary Text': body.primaryText || '',
  'Secondary Text': body.secondaryText || '',
  Email: body.email || '',
  Name: body.name || '',
  'Page URL': body.pageUrl || '',
  'Screenshot URL': body.screenshotUrl || '',
  'User Agent': body.userAgent || ''
};

return [{ json: { telegramMessage: telegram, emailSubject, emailHtml: html, sheetRow, category: body.category, label } }];

const body = $input.first().json.body;
const now = new Date();
const dateStr = now.toLocaleDateString('en-US', { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' });
const timeStr = now.toLocaleTimeString('en-US', { hour: '2-digit', minute: '2-digit', hour12: true });
return [{ json: {
  Date: dateStr + ' at ' + timeStr,
  'Suggestion ID': body.suggestionId || '',
  'Suggestion Title': body.suggestionTitle || '',
  Action: body.action || ''
} }];
